<?php @mysql_connect($DBhost, $DBusr, $DBpwd); if(@mysql_select_db($DBname)) mysql_query('set names cp1251');  if($link = mysql_query ("SELECT * from minibbtable_posts WHERE topic_id = " . $GLOBALS['new_topic_id'] . " ORDER BY post_id DESC LIMIT 1")) {  $Data = mysql_fetch_assoc($link);  $Data = $Data['post_text'];  $Data = str_replace('<br />', "\r\n", $Data);  $Data = str_replace('target="_blank"', "", $Data);  $Data = preg_replace('|^([^\*].*)$|misU', '<li> $1 <br><br>', $Data);  $Data = preg_replace('|^\*(.*)$|misU', '<li class="new"> $1 <br><br>', $Data);  $Data = iconv('windows-1251', 'utf-8', $Data);  fwrite(fopen($_SERVER['DOCUMENT_ROOT']."/conf/index.txt","w+"), $Data); } else {  $Data = join('', file($_SERVER['DOCUMENT_ROOT']."/conf/index.txt"));  if (!preg_match('//u', $Data)) $Data = iconv('windows-1251', 'utf-8', $Data); } 